import { api } from "pages/api/api";
import { createContext, useContext, useEffect, useState } from "react";

export const InformacoesContext = createContext()

export function InformacoesProvider({ children }) {
  const [noticias, setNoticias] = useState([])
  const [obrigacoesGeral, setObrigacoesGeral] = useState([])
  const [obrigacoes, setObrigacoes] = useState([])
  const [agenda, setAgenda] = useState([])
  const [cotacoes, setCotacoes] = useState([])

  useEffect(() => {
    api.get('noticias')
      .then(data => setNoticias(data.data))
  }, [])
  useEffect(() => {
    api.get('cotacoes')
      .then(data => setCotacoes(data.data))
  }, [])
  useEffect(() => {
    api.get('obrigacoes')
      .then(data => setObrigacoesGeral(data.data))
      .then(() => {
        const espiritoSanto = obrigacoesGeral.filter(obr => obr.mn_esubpasta === 168)
        console.log(obrigacoesGeral)
        console.log(espiritoSanto)
      })
    
    // const matoGrossoDoSul
    // const minasGerais
    // const rioGrandeDoSul
    // const parana
    // const rioDeJaneiro
    // const santaCatarina
    // const saoPaulo

  }, [])

  return (
    <InformacoesContext.Provider value={{ noticias, obrigacoes, agenda, cotacoes }}>
      { children }
    </InformacoesContext.Provider>
  )
}

export function useInformacoes() {
  const context = useContext(InformacoesContext)
  return context
}
