/** @jsx jsx */
import SectionHeading from 'components/section-heading';
import Service from 'components/cards/service';
import ScrollAnimation from 'react-animate-on-scroll';
import { ThemeProvider, jsx, Box, Container  } from 'theme-ui';
import theme from 'theme';
import Layout from 'components/layout';
import BannerSecundario from 'sections/bannerSecundario';
import banner from 'assets/images/banner1.png';
import { useInformacoes } from 'hooks/useInformacoes';
import { slugify } from 'services/slugify';

export default function Noticia({ noticia }) {
  const { noticias } = useInformacoes()
  const noticiaAtual = noticias.find(n => slugify(n.mn_titulo) === noticia.slug)

  return (
    <ThemeProvider theme={theme}>
      <Layout>
        <BannerSecundario title={noticiaAtual?.mn_titulo} banner={banner} />
        {/* <h1>teste - { noticiaAtual?.mn_texto }</h1> */}
        <Box as="section" id="services" sx={styles.section}>
          <Container>
          <ScrollAnimation animateIn="fadeIn" animateOut="fadeOut">
              <SectionHeading
                slogan=""
                title="Indicadores EconÃ´micos"
              />
              <Box sx={styles.grid}>
                {services.map((service, i) => (
                  <Service key={i} service={service} />
                ))}
              </Box>
            </ScrollAnimation>
          </Container>
        </Box>
      </Layout>
    </ThemeProvider>
  );
}

const styles = {
  section: {
    pt: [8, null, null, null, 10, 12],
    pb: [12, null, null, null, null, 15],
  },
  grid: {
    gap: [3, null, null, 4],
    display: 'grid',
    justifyContent: 'center',
    gridTemplateColumns: [
      'repeat(2, 1fr)',
      null,
      null,
      'repeat(3, 1fr)',
      null,
      'repeat(4, 1fr)',
      'repeat(4, 300px)',
    ],
  },
};

export const getStaticPaths = async () => {
  return {
    paths: [],
    fallback: 'blocking'
  }
}

export const getStaticProps = async ({ params }) => {
  const { slug } = params
  return {
    props: {
      noticia: { slug }
    }
  }
}
