  return (
    <Box as="section" sx={styles.section} >
      <Container>
        <Heading as="h2">{title}</Heading>
      </Container>
    </Box>
  );
};

export default BannerSecundario;

const styles = {
  section: {
    backgroundColor: 'primary',
    backgroundImage: `url(${banner})`,
    pt: [17, null, null, 20, null],
    pb: [6, null, null, 12, 16],
