import { ThemeProvider, Slider } from 'theme-ui';
import theme from 'theme';
import Layout from 'components/layout';
import BannerSecundario from 'sections/bannerSecundario';
import banner from 'assets/images/banner1.png'
import SlickArrow from 'components/slick-arrow';
import { useInformacoes } from 'hooks/useInformacoes';

const settings = {
  slidesToShow: 3,
  slidesToScroll: 1,
  speed: 500,
  nextArrow: <SlickArrow control="next" />,
  prevArrow: <SlickArrow control="prev" />,
  responsive: [
    {
      breakpoint: 100000,
      settings: 'unslick',
    },
    {
      breakpoint: 768,
      settings: {
        infinite: false,
        slidesToShow: 2,
        slidesToScroll: 1,
      },
    },
    {
      breakpoint: 767,
      settings: 'unslick',
    },
  ],
};

export default function Noticias() {
  const { noticias } = useInformacoes()

  return (
    <ThemeProvider theme={theme}>
      <Layout>
        <BannerSecundario title="NotÃ­cias" banner={banner} />
        <Slider sx={styles.grid} {...settings}>
          {noticias?.map((post) => (
            <BlogPost key={post.mn_codigo} post={post} />
          ))}
        </Slider>
      </Layout>
    </ThemeProvider>
  );
}
